import React from "react";
import { Text, View, StyleSheet, TouchableOpacity } from 'react-native';
import { Row, Col } from 'react-native-responsive-grid-system';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import AppColorTemplate from '../styles/ColorConfig';

const WelcomeScreen = (props) => {
    return (<View style={{ backgroundColor:'#fff', padding:3, 
    borderBottomWidth:1, borderColor:'#ddd' }}>
      <Row>
        <Col xs={2} sm={2} md={2} lg={2}>
          <TouchableOpacity onPress={()=>{ props.navigation.toggleDrawer(); }} >
            <MaterialIcons name="menu-open" style={styles.iconMenuOpen} size={36} />
          </TouchableOpacity>
        </Col>
        <Col xs={4} sm={4} md={4} lg={4}>
          <Text style={{ padding:10 }}>Abc</Text>
        </Col>
        <Col xs={4} sm={4} md={4} lg={4}>
          <Text style={{ padding:10 }}>Def</Text>
        </Col>
      </Row>
      </View>
    );
}

const styles = StyleSheet.create({ 
  iconMenuOpen:{ color:AppColorTemplate.GLOBAL_TMPL_COLOR1_MAX },
 });

export default WelcomeScreen;
